import axios from 'axios';

const instance = axios.create({
  baseURL: process.env.VUE_APP_COORDINATOR_API || 'http://localhost:8080'
});

export default instance;

// 参与方注册
export function register(data) {
  return axios.post('/register', data);
}

// 获取CKKS参数
export function getCKKSParams() {
  return axios.get('/params/ckks');
}

// 上传公钥份额
export function uploadPublicKey(data) {
  return axios.post('/keys/public', data);
}

// 上传私钥
export function uploadSecretKey(data) {
  return axios.post('/keys/secret', data);
}

// 上传伽罗瓦密钥份额
export function uploadGaloisKey(data) {
  return axios.post('/keys/galois', data);
}

// 上传重线性化密钥份额
export function uploadRelinearizationKey(data) {
  return axios.post('/keys/relin', data);
}

// 获取重线性化密钥第一轮聚合结果
export function getRelinearizationKeyRound1Aggregated() {
  return axios.get('/keys/relin/round1');
}

// 获取聚合后的密钥
export function getAggregatedKeys() {
  return axios.get('/keys/aggregated');
}

// 获取参与方统计信息
export function getParticipants() {
  return axios.get('/participants');
}

// 获取密钥生成进度
export function getSetupStatus() {
  return axios.get('/setup/status');
}

// 上报参与方URL
export function reportParticipantURL(data) {
  return axios.post('/participants/url', data);
}

// 获取参与方详细列表
export function getParticipantsList() {
  return axios.get('/participants/list');
}

// 心跳包
export function heartbeat(data) {
  return axios.post('/heartbeat', data);
}

// 获取在线参与方
export function getOnlineParticipants() {
  return axios.get('/participants/online');
}

// 获取在线状态
export function getOnlineStatus() {
  return axios.get('/status/online');
}

// 获取详细状态
export function getDetailedStatus() {
  return axios.get('/status');
}

// 测试所有密钥
export function testAllKeys(data) {
  return axios.post('/test/all', data);
}

// 测试公钥
export function testPublicKey(data) {
  return axios.post('/test/public', data);
}

// 测试重线性化密钥
export function testRelinearizationKey(data) {
  return axios.post('/test/relin', data);
}

// 测试伽罗瓦密钥
export function testGaloisKeys(data) {
  return axios.post('/test/galois', data);
}

// 获取重线性化密钥状态
export function getRelinearizationKeyStatus() {
  return axios.get('/keys/relin/status');
}

// 注销参与方
export function unregister(data) {
  return axios.post('/unregister', data);
}
